import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import API_BASE_URL from "../api";

function CaptchaTest() {
  const navigate = useNavigate();
  const [keyData, setKeyData] = useState([]);
  const [mouseData, setMouseData] = useState([]);
  const [sessionStartTime, setSessionStartTime] = useState(Date.now());
  const [scrollSpeed, setScrollSpeed] = useState(0);
  const [phrase, setPhrase] = useState("Type this phrase exactly!");
  const [inputText, setInputText] = useState("");
  const [learningPhase, setLearningPhase] = useState(true);
  const [learningTimeLeft, setLearningTimeLeft] = useState(60);

  useEffect(() => {
    // Countdown Timer for Learning Phase
    const timer = setInterval(() => {
      setLearningTimeLeft((prev) => {
        if (prev <= 1) {
          setLearningPhase(false);
          clearInterval(timer);
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    let lastMousePos = { x: 0, y: 0, time: Date.now() };
    let lastScrollTime = Date.now();

    const handleKeyDown = (event) => {
      const now = Date.now();
      setKeyData((prev) => [
        ...prev,
        { key: event.key, time: now - sessionStartTime },
      ]);
    };

    const handleMouseMove = (event) => {
      const now = Date.now();
      const timeDiff = now - lastMousePos.time;
      const dx = event.clientX - lastMousePos.x;
      const dy = event.clientY - lastMousePos.y;
      const distance = Math.sqrt(dx * dx + dy * dy);
      const speed = timeDiff > 0 ? distance / timeDiff : 0;
      const angle = Math.atan2(dy, dx) * (180 / Math.PI);

      setMouseData((prev) => [...prev, { speed, angle, distance, time: now }]);
      lastMousePos = { x: event.clientX, y: event.clientY, time: now };
    };

    const handleScroll = () => {
      const now = Date.now();
      const timeDiff = now - lastScrollTime;
      const speed = timeDiff > 0 ? window.scrollY / timeDiff : 0;
      setScrollSpeed(speed);
      lastScrollTime = now;
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleSubmit = async () => {
    if (inputText !== phrase) {
      alert("Please type the phrase correctly.");
      return;
    }

    const sessionTime = (Date.now() - sessionStartTime) / 1000; // Convert to seconds

    const requestBody = {
      username: localStorage.getItem("username") || "unknown_user",
      keyData,
      mouseData,
      sessionTime,
      scrollSpeed,
    };

    const response = await fetch(`${API_BASE_URL}/validate_captcha`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestBody),
    });

    const data = await response.json();
    if (data.anomaly) {
      alert("Anomaly detected! Logging you out.");
      navigate("/logout");
    } else {
      navigate("/dashboard");
    }
  };

  return (
    <div>
      <h2>Behavioral CAPTCHA</h2>
      {learningPhase ? (
        <p>Learning Phase: {learningTimeLeft} seconds remaining...</p>
      ) : (
        <>
          <p>Type this phrase: <strong>{phrase}</strong></p>
          <input type="text" value={inputText} onChange={(e) => setInputText(e.target.value)} />
          <button onClick={handleSubmit}>Submit</button>
        </>
      )}
    </div>
  );
}

export default CaptchaTest;
